document.addEventListener("DOMContentLoaded",()=>{document.querySelector("#new-note-form").addEventListener("submit",handleNewNoteFormSubmit);document.querySelector("#delete-all").addEventListener("click",handleDeleteAllClick);const e=localStorage.getItem("notes");if(e){console.log("persistedNotes",e);const t=JSON.parse(e);console.log("notes",t);const n=document.querySelector("#notes-list");Object.values(t).forEach(e=>{n.appendChild(createNote(e))})}else localStorage.setItem("notes",JSON.stringify({}))});const createNote=function({noteTitle:e,noteText:t,noteColour:n,noteID:o}){const l=document.createElement("li");l.classList.add("note-list-item");const c=document.createElement("h2");c.textContent=e,l.appendChild(c);const a=document.createElement("p");a.textContent=t,l.appendChild(a);const s=document.createElement("button");return s.innerHTML='<i class="fas fa-times"></i>',s.onclick=()=>handleDeleteClick(o),l.appendChild(s),l.setAttribute("id",o),l.setAttribute("class","note"),l.setAttribute("data",n),l},handleNewNoteFormSubmit=function(e){e.preventDefault();const{target:{noteTitle:{value:t},noteText:{value:n},noteColour:{value:o}}}=e,l=(new Date).toString(),c={noteTitle:t,noteText:n,noteColour:o,noteID:l},a=createNote(c);document.querySelector("#notes-list").appendChild(a);const s=JSON.parse(localStorage.getItem("notes"));localStorage.setItem("notes",JSON.stringify({...s,[l]:c})),e.target.reset()},handleDeleteAllClick=function(){const e=document.querySelector("#notes-list");localStorage.setItem("notes",JSON.stringify({})),e.innerHTML=""},handleDeleteClick=function(e){document.getElementById(e).remove();const t=JSON.parse(localStorage.getItem("notes")),{[e]:n,...o}=t;localStorage.setItem("notes",JSON.stringify(o))},handleFilterByColour=function(){},handleFilterByOldNew=function(){},handleFilterByNewOld=function(){},handleFilterByAlphabetical=function(){};